import logo from './logo.svg';
import './App.css';
import { Component } from 'react';

class App extends Component{
  constructor(){
    super();

this.state={
  monsters:[]
}
// console.log(1)
 }

componentDidMount(){
// console.log(3)

  fetch('https://jsonplaceholder.typicode.com/users')
  .then(res=>res.json())
  .then(user=>this.setState(()=>{
    return {monsters:user}}
  ))
}

  render(){
// console.log(2)

      return (

        <div className='App'>
          <input className='search' placeholder='search monsters' type='search'
          onChange={(event)=>{console.log(event.target.value);
          let searchInput=event.target.value.toLocaleLowerCase();
          let filterMonsters=this.state.monsters.filter(el=>{
            return el.name.toLocaleLowerCase().includes(searchInput)
          });this.setState(()=>{
            return {monsters: filterMonsters}
          })}}/>
          {
             this.state.monsters.map(el=>{
              return(
                <div key={el.id}>
                <h1>{el.name}</h1>
              </div>)
            })


          }
        </div>


      )
  }


}

